var deslocX = 100;
var deslocY = 100;

//Width and height
var w = 800 + deslocX;
var h = 600 + deslocY;

//Create SVG element
var svg = d3.select("body")
	.append("svg")
	.attr("width", w)
	.attr("height", h);

//Create and modify dataset
inter_war = inter_war.war_data;
dataset = generateClassesDataset(inter_war, 15)
dataset = dataset.map(function(d){
	return {
		id: d.war_id,
		name: d.war_name,
		x: (d.end_date - d.start_date)/(3600*24000),
		y: d.total_deaths,
		class: d.start_date/(3600*24000)
	}
})


var scat = new Scatterplot({
	id: "myGeoMap",
	container: svg,
	x: deslocX,
	y: deslocY,
	width: w - deslocX,
	height: h - deslocY,
	dataset: dataset
});
